CREATE TABLE NODE_CONFIRMATION(id VARCHAR(64) PRIMARY KEY, request_id VARCHAR(32), concentrator VARCHAR(64) NOT NULL, node VARCHAR(16) NOT NULL, lan_id VARCHAR(4) NOT NULL, communication_net VARCHAR(8) NOT NULL, address VARCHAR(32) NOT NULL, confirmer VARCHAR(16), requested_time TIMESTAMP NOT NULL, expired_time TIMESTAMP NOT NULL, confirmed_time TIMESTAMP, canceled BOOLEAN DEFAULT FALSE NOT NULL);
CREATE TABLE CONCENTRATION(id VARCHAR(64) PRIMARY KEY, concentrator VARCHAR(64) NOT NULL, node VARCHAR(16) NOT NULL, lan_id VARCHAR(4) NOT NULL, communication_net VARCHAR(8) NOT NULL, address VARCHAR(32) NOT NULL, confirmation_time TIMESTAMP NOT NULL);
CREATE INDEX INDEX_NODE_CONFIRMATION_CONCENTRATOR_NODE ON NODE_CONFIRMATION (concentrator, node);
CREATE UNIQUE INDEX INDEX_CONCENTRATION_CONCENTRATOR_NODE ON CONCENTRATION (concentrator, node);